<script>
    import {
        createEventDispatcher,
        onDestroy,
        onMount
    } from "svelte";
    
    let map
    export let province
    let mapActive = false
    let provinces
    let activeText
    let activeElements
    const dispatch = createEventDispatcher()

    onMount(() => {
        provinces = {
            friesland: {
                paths: map.querySelectorAll(".friesland"),
                text: map.querySelector("#friesland-text"),
            },
            groningen: {
                paths: map.querySelectorAll(".groningen"),
                text: map.querySelector("#groningen-text"),
            },
            drenthe: {
                paths: map.querySelectorAll(".drenthe"),
                text: map.querySelector("#drenthe-text"),
            },
            overijssel: {
                paths: map.querySelectorAll(".overijssel"),
                text: map.querySelector("#overijssel-text"),
            },
            gelderland: {
                paths: map.querySelectorAll(".gelderland"),
                text: map.querySelector("#gelderland-text"),
            },
            limburg: {
                paths: map.querySelectorAll(".limburg"),
                text: map.querySelector("#limburg-text"),
            },
            northbrabant: {
                paths: map.querySelectorAll(".north-brabant"),
                text: map.querySelector("#north-brabant-text"),
            },
            utrecht: {
                paths: map.querySelectorAll(".utrecht"),
                text: map.querySelector("#utrecht-text"),
            },
            flevoland: {
                paths: map.querySelectorAll(".flevoland"),
                text: map.querySelector("#flevoland-text"),
            },
            northholland: {
                paths: map.querySelectorAll(".north-holland"),
                text: map.querySelector("#north-holland-text"),
            },
            southholland: {
                paths: map.querySelectorAll(".south-holland"),
                text: map.querySelector("#south-holland-text"),
            },
            zeeland: {
                paths: map.querySelectorAll(".zeeland"),
                text: map.querySelector("#zeeland-text"),
            },
        }

        Object.keys(provinces).forEach(provin => {
            provinces[provin].paths.forEach(elm => {
                AddListener(elm, provinces, provin)
            })
            provinces[provin].text.addEventListener("click", () => {
                Dispatch(provinces[provin], provin)
            })
        });

    })
    
    onDestroy(() => {
        if (provinces) {
            Object.keys(provinces).forEach(provin => {
                provinces[provin].paths.forEach(elm => {
                    RemoveListener(elm, provinces, provin)
                })
                provinces[provin].text.removeEventListener("click", () =>{
                    Dispatch(provinces[provin], provin)
                })
            });
        }
    })

    function Dispatch(data, name) {
        resetMap()
        activeElements = data.paths
        activeText = data.text
        activeElements.forEach(elm => {
            elm.classList.add("map-path-active")
        })
        activeText.classList.add("map-text-active")
        mapActive = true
        dispatch("mapClick", {data: data, key: name})
    }

    function resetMap() {
        if (activeElements && activeText) {
            activeElements.forEach(elm => {
                elm.classList.remove("map-path-active")
            })
            activeText.classList.remove("map-text-active")
        }
    }
    
    function AddListener(elm, data, key) {
        elm.addEventListener("click", () => {
            Dispatch(data[key], key)
        })
    }
    
    function RemoveListener(elm, data, key) {
        elm.removeEventListener("click", () => {
            Dispatch(data[key], key)
        })
    }

    $: {
        if (province == "") {
            mapActive = false
            resetMap()
        }
    }

</script>